mapper.insert:
INSERT INTO EA_STEP
	(
	 EA_STEPNO
	,EA_EMPNO
	,EA_EMPNO1
	,EA_EMPNO2
	,EA_LINENO
	)
VALUES
	(
	 #{ea_stepno, jdbcType=VARCHAR}
	,#{ea_empno, jdbcType=VARCHAR}
	,#{ea_empno1, jdbcType=VARCHAR}
	,#{ea_empno2, jdbcType=VARCHAR}
	,#{ea_lineno,jdbcType=VARCHAR}
	)

mapper.select:
SELECT 		A.*
FROM 		EA_STEP A
WHERE 		EA_STEPNO=#{ea_stepno, jdbcType=VARCHAR}

mapper.insert:
INSERT INTO EA_TABLE
