	 'H'
	,#{ea_lineno, jdbcType=VARCHAR}
	,#{ea_logno, jdbcType=VARCHAR}
	,#{ea_no, jdbcType=VARCHAR}
	,#{ea_subject, jdbcType=VARCHAR}
	,#{ea_memo, jdbcType=VARCHAR}
	,#{eacno, jdbcType=VARCHAR}
	,#{ea_empno, jdbcType=VARCHAR}
	,'01'
	,SYSDATE
	,#{ea_nextemno, jdbcType=VARCHAR}
	)

INSERT INTO EA_TABLE
	(
	 EA_DOCCD
	,EA_LINENO
	,EA_LOGNO
	,EA_NO
	,EA_SUBJECT
	,EA_MEMO
	,EADRAFTCONTANT
	,EA_EMPNO
	,EA_STATUS
	,EA_INSERTDATE
	,EA_NEXTEMNO
	)
VALUES
	(
	 'P'
	,#{ea_lineno, jdbcType=VARCHAR}
	,#{ea_logno, jdbcType=VARCHAR}
	,#{ea_no, jdbcType=VARCHAR}
	,#{ea_subject, jdbcType=VARCHAR}
	,#{ea_memo, jdbcType=VARCHAR}
	,#{eadraftcontant, jdbcType=VARCHAR}
	,#{ea_empno, jdbcType=VARCHAR}
	,'01'
	,SYSDATE
	,#{ea_nextemno, jdbcType=VARCHAR}
	)

mapper.select:
SELECT * FROM ( SELECT AA.* ,CEIL(ROW_NUMBER() OVER(ORDER BY AA.EA_NO DESC)/#{pageSize, jdbcType=VARCHAR}) PAGENO ,COUNT(AA.EA_NO) OVER() AS TOTALCOUNT FROM ( SELECT B.EA_NO EA_NO ,B.EA_LINENO EA_LINENO ,B.EA_LOGNO EA_LOGNO ,B.EA_DOCCD EA_DOCCD ,B.EA_EMPNO EA_EMPNO ,B.EA_SUBJECT EA_SUBJECT ,B.EA_INSERTDATE EA_INSERTDATE ,B.EA_STATUS EA_STATUS ,B.EA_NEXTEMNO EA_NEXTEMNO ,C.EA_STEPNO EA_STEPNO ,G.EMNAME EMNAME FROM (SELECT A.* FROM EA_TABLE A WHERE A.EA_NEXTEMNO=#{emno, jdbcType=VARCHAR} AND A.EA_STATUS IN('01', '02', '03', '04', '05', '06', '07') ORDER BY A.EA_INSERTDATE DESC)B ,EA_LOG C ,(SELECT D.* FROM EM_INFO D, EA_TABLE E, EA_LOG F WHERE D.EMNO=E.EA_EMPNO AND EA_NEXTEMNO=#{emno, jdbcType=VARCHAR} AND E.EA_LOGNO=F.EA_LOGNO AND ROWNUM='1' ORDER BY E.EA_NO DESC)G WHERE C.EA_LOGNO=B.EA_LOGNO
AND TO_CHAR(EA_INSERTDATE, 'YYYYMMDD') >= TO_DATE(#{st_date},'YYYYMMDD') AND TO_CHAR(EA_INSERTDATE, 'YYYYMMDD') <= TO_DATE(#{ed_date},'YYYYMMDD')+1
)AA ) WHERE PAGENO=#{curPage, jdbcType=VARCHAR}
