mapper.select:
/* search 적용된 내 게시판: 진행 */ SELECT * FROM ( SELECT AA.* ,CEIL(ROW_NUMBER() OVER(ORDER BY AA.EA_NO DESC)/#{pageSize, jdbcType=VARCHAR}) PAGENO ,COUNT(AA.EA_NO) OVER() AS TOTALCOUNT FROM ( SELECT B.EA_STATUS EA_STATUS ,A.EA_NO EA_NO ,A.EA_STEPNO EA_STEPNO ,A.EA_LINENO EA_LINENO ,A.EA_LOGNO EA_LOGNO ,A.EA_FINALDATE EA_FINALDATE ,TO_CHAR(B.EA_INSERTDATE,'YYYY-MM-DD') EA_INSERTDATE ,B.EA_EMPNO EA_EMPNO ,B.EA_SUBJECT EA_SUBJECT ,B.EA_DOCCD EA_DOCCD ,C.EMNAME EMNAME FROM EA_LOG A ,EA_TABLE B ,(SELECT C.EMNAME EMNAME FROM EM_INFO C WHERE C.EMNO=#{emno, jdbcType=VARCHAR})C WHERE B.EA_EMPNO=#{emno, jdbcType=VARCHAR} AND A.EA_LOGNO=B.EA_LOGNO AND (B.EA_STATUS IN('01', '02'))
AND B.EA_DOCCD LIKE '%'||#{keyword}||'%'
AND B.EA_DOCCD IN('H','P','R','S')
AND TO_CHAR(B.EA_INSERTDATE,'YYYYMMDD') >= TO_DATE(#{st_date},'YYYYMMDD') AND TO_CHAR(B.EA_INSERTDATE,'YYYYMMDD') < TO_DATE(#{ed_date},'YYYYMMDD')+1
)AA ) WHERE PAGENO=#{curPage, jdbcType=VARCHAR}

/* search 적용 easelectallsb */ SELECT * FROM ( SELECT AA.* ,CEIL(ROW_NUMBER() OVER(ORDER BY AA.EA_NO DESC)/#{pageSize, jdbcType=VARCHAR}) PAGENO ,COUNT(AA.EA_NO) OVER() AS TOTALCOUNT FROM ( SELECT B.EA_STATUS EA_STATUS ,A.EA_NO EA_NO ,A.EA_STEPNO EA_STEPNO ,A.EA_LINENO EA_LINENO ,A.EA_LOGNO EA_LOGNO ,A.EA_FINALDATE EA_FINALDATE ,TO_CHAR(B.EA_INSERTDATE,'YYYY-MM-DD') EA_INSERTDATE ,B.EA_EMPNO EA_EMPNO ,B.EA_SUBJECT EA_SUBJECT ,B.EA_DOCCD EA_DOCCD ,C.EMNAME EMNAME FROM EA_LOG A ,EA_TABLE B ,(SELECT C.EMNAME EMNAME FROM EM_INFO C WHERE C.EMNO=#{emno, jdbcType=VARCHAR})C WHERE B.EA_EMPNO=#{emno, jdbcType=VARCHAR} AND A.EA_LOGNO=B.EA_LOGNO AND (B.EA_STATUS IN('05'))
AND B.EA_DOCCD LIKE '%'||#{keyword}||'%'
AND B.EA_DOCCD IN('H','P','R','S')
AND TO_CHAR(B.EA_INSERTDATE,'YYYYMMDD') >= TO_DATE(#{st_date},'YYYYMMDD') AND TO_CHAR(B.EA_INSERTDATE,'YYYYMMDD') < TO_DATE(#{ed_date},'YYYYMMDD')+1
)AA ) WHERE PAGENO=#{curPage, jdbcType=VARCHAR}

