)AA ) WHERE PAGENO=#{curPage, jdbcType=VARCHAR}

/* search 적용 04 게시판*/ SELECT * FROM ( SELECT AA.* ,CEIL(ROW_NUMBER() OVER(ORDER BY AA.EA_NO DESC)/#{pageSize, jdbcType=VARCHAR}) PAGENO ,COUNT(AA.EA_NO) OVER() AS TOTALCOUNT FROM ( SELECT B.EA_STATUS EA_STATUS ,A.EA_NO EA_NO ,A.EA_STEPNO EA_STEPNO ,A.EA_LINENO EA_LINENO ,A.EA_LOGNO EA_LOGNO ,A.EA_FINALDATE EA_FINALDATE ,TO_CHAR(B.EA_INSERTDATE,'YYYY-MM-DD') EA_INSERTDATE ,B.EA_DOCCD EA_DOCCD ,B.EA_EMPNO EA_EMPNO ,B.EA_SUBJECT EA_SUBJECT ,C.EMNAME EMNAME FROM EA_LOG A ,EA_TABLE B ,(SELECT C.EMNAME EMNAME FROM EM_INFO C WHERE C.EMNO=#{emno, jdbcType=VARCHAR})C WHERE B.EA_EMPNO=#{emno, jdbcType=VARCHAR} AND A.EA_LOGNO=B.EA_LOGNO AND (B.EA_STATUS IN('04'))
AND B.EA_DOCCD LIKE '%'||#{keyword}||'%'
AND B.EA_DOCCD IN('H','P','R','S')
AND TO_CHAR(B.EA_INSERTDATE,'YYYYMMDD') >= TO_DATE(#{st_date},'YYYYMMDD') AND TO_CHAR(B.EA_INSERTDATE,'YYYYMMDD') < TO_DATE(#{ed_date},'YYYYMMDD')+1
)AA ) WHERE PAGENO=#{curPage, jdbcType=VARCHAR}

/* search+페이징용 selectAll */ SELECT * FROM ( SELECT AA.* ,CEIL(ROW_NUMBER() OVER(ORDER BY AA.EA_NO DESC)/#{pageSize, jdbcType=VARCHAR}) PAGENO ,COUNT(AA.EA_NO) OVER() AS TOTALCOUNT FROM ( SELECT A.EA_STATUS EA_STATUS ,B.EA_NO EA_NO ,A.EA_STEPNO EA_STEPNO ,A.EA_LINENO EA_LINENO ,A.EA_LOGNO EA_LOGNO ,A.EA_FINALDATE EA_FINALDATE ,TO_CHAR(B.EA_INSERTDATE,'YYYY-MM-DD') EA_INSERTDATE ,B.EA_DOCCD EA_DOCCD ,B.EA_EMPNO EA_EMPNO ,B.EA_SUBJECT EA_SUBJECT ,C.EMNAME EMNAME FROM EA_LOG A ,EA_TABLE B ,(SELECT C.EMNAME EMNAME FROM EM_INFO C WHERE C.EMNO=#{emno, jdbcType=VARCHAR})C WHERE B.EA_EMPNO=#{emno, jdbcType=VARCHAR} AND A.EA_LOGNO=B.EA_LOGNO AND (B.EA_STATUS IN('01', '02', '03', '04', '05', '07', '06'))
AND B.EA_DOCCD LIKE '%'||#{keyword}||'%'
AND B.EA_DOCCD IN('H','P','R','S')
AND TO_CHAR(B.EA_INSERTDATE,'YYYYMMDD') >= TO_DATE(#{st_date},'YYYYMMDD') AND TO_CHAR(B.EA_INSERTDATE,'YYYYMMDD') < TO_DATE(#{ed_date},'YYYYMMDD')+1
)AA ) WHERE PAGENO=#{curPage, jdbcType=VARCHAR}

/*search용 내가 결재자인 문서리스트 게시판*/ SELECT * FROM (SELECT D.* ,CEIL(ROW_NUMBER() OVER(ORDER BY D.EA_NO DESC)/#{pageSize, jdbcType=VARCHAR}) PAGENO ,COUNT(D.EA_NO) OVER() AS TOTALCOUNT FROM(SELECT B.EA_NO EA_NO ,B.EA_LINENO EA_LINENO ,B.EA_LOGNO EA_LOGNO ,B.EA_DOCCD EA_DOCCD ,B.EA_EMPNO EA_EMPNO ,B.EA_SUBJECT EA_SUBJECT ,TO_CHAR(B.EA_INSERTDATE,'YYYY-MM-DD') EA_INSERTDATE ,B.EA_STATUS EA_STATUS ,B.EA_NEXTEMNO EA_NEXTEMNO ,C.EA_STEPNO EA_STEPNO FROM (SELECT A.* FROM EA_TABLE A WHERE A.EA_NEXTEMNO=#{emno, jdbcType=VARCHAR} AND A.EA_STATUS IN('01','02')
AND A.EA_DOCCD LIKE '%'||#{keyword}||'%'
AND A.EA_DOCCD IN('H','P','R','S')
AND TO_CHAR(A.EA_INSERTDATE,'YYYYMMDD') >= TO_DATE(#{st_date},'YYYYMMDD') AND TO_CHAR(A.EA_INSERTDATE,'YYYYMMDD') < TO_DATE(#{ed_date},'YYYYMMDD')+1
ORDER BY A.EA_INSERTDATE DESC)B ,EA_LOG C WHERE C.EA_LOGNO=B.EA_LOGNO)D) WHERE PAGENO=#{curPage, jdbcType=VARCHAR}

/* 채번 쿼리 */
SELECT 		A.*
FROM 		EA_STEP A
WHERE 		EA_STEPNO=#{ea_stepno, jdbcType=VARCHAR}
